var widthWebIdxJq = $(window).width();

if (widthWebIdxJq <= 654) {
  $(document).ready(() => {
    $("#showMenuUserIdx").click(() => {
      $("#userMenuLeftIdx").show("slider");
      $("#userMenuLeftIdx").width("100%");
      $("#userMenuRightIdx").hide("slider");
    });
  });
  $(document).ready(() => {
    $(".content__main___wp____item").click(() => {
      $("#userMenuLeftIdx").hide("slider");
      $("#userMenuRightIdx").width("100%");
      $("#userMenuRightIdx").show("slider");
    });
  });
}

if (widthWebIdxJq > 786) {
  let elementSpanMenuFx = $(".name__menuFxBar_w");
  $(document).ready(() => {
    $("#showMenuBar").click(() => {
      if ($(".menuFxBar").width() === 60) {
        $(".menuFxBar").width("230px");
        $("#page__topbar").css("left", "230px");
        $("#page__topbar").css("width", "calc(100% - 230px)");

        $("#menuDropdownAdmin").css("display", "none");
        $(".page__main").css("margin-left", "172px");

        for (let element of elementSpanMenuFx) {
          $("." + element.classList[1]).css("display", "block");
        }
      } else if ($(".menuFxBar").width() === 230) {
        $(".menuFxBar").width("60px");
        $("#page__topbar").css("width", "calc(100% - 60px)");
        $("#page__topbar").css("left", "60px");
        $("#menuDropdownAdmin").css("display", "");
        $(".page__main").css("margin-left", "0");
        for (let element of elementSpanMenuFx) {
          $("." + element.classList[1]).css("display", "none");
        }
      }
    });
  });
} else {
  $(document).ready(() => {
    $("#showMenuBar").click(() => {
      if ($(".menuFxBar").hasClass("slideInAdm")) {
        // $(".menuFxBar").toggleClass("slideOutAdm");
        $(".menuFxBar").toggleClass("slideOutAdm");
      } else {
        $(".menuFxBar").toggleClass("slideInAdm");
      }
      $(document).on("click", (event) => {
        var $trigger = $("#showMenuBar");
        var $trigger2 = $(".menuFxBar");
        if (
          $trigger !== event.target &&
          !$trigger.has(event.target).length &&
          $trigger2 !== event.target &&
          !$trigger2.has(event.target).length
        ) {
          if (!$(".menuFxBar").hasClass("slideOutAdm"))
            $(".menuFxBar").toggleClass("slideOutAdm");
        }
      });
    });
  });
}

$(document).ready(() => {
  $("#showMenuBrand").click(() => {
    if ($(".main__brand___list____main").css("display") != "none") {
      $(".main__brand___list____main").hide("slider");
      $(".fa-size-menuLeftBrand").addClass("tranForm-rotate-90");
      $(".fa-size-menuLeftBrand").removeClass("tranForm-rotate-0");
    } else {
      $(".main__brand___list____main").show("slider");
      $(".fa-size-menuLefBrand").addClass("tranForm-rotate-0");
      $(".fa-size-menuLeftBrand").removeClass("tranForm-rotate-90");
    }
  });
  $("#showMenuDiscount").click(() => {
    if ($(".main__discount___list____main").css("display") != "none") {
      $(".main__discount___list____main").hide("slider");
      $(".fa-size-menuLeftDiscount").addClass("tranForm-rotate-90");
      $(".fa-size-menuLeftDiscount").removeClass("tranForm-rotate-0");
    } else {
      $(".main__discount___list____main").show("slider");
      $(".fa-size-menuLeftDiscount").addClass("tranForm-rotate-0");
      $(".fa-size-menuLeftDiscount").removeClass("tranForm-rotate-90");
    }
  });
});

